import { useState } from 'react'

const Header = (props) => {
  const {title} = props;

  return <h2>{title}</h2>
}

const Person = (props) => {
  const {personObject} = props
  return <div>{personObject.name} {personObject.number}</div>
}

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '(123) 456-7890' }
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')

  const addPerson = (event) => {
    event.preventDefault()
    const personObject = {
      name: newName,
      number: newNumber
    }

    const uniqueNames = new Set(persons.map(p => p.name))

    if (uniqueNames.has(newName)) {
      alert(`${newName} is already added to phonebook`)
    } else {
      setPersons(persons.concat(personObject))
      setNewName('')
    }
  }

  const handleNameChange = (event) => setNewName(event.target.value)
  const handleNumberChange = (event) => setNewNumber(event.target.value)
  const handleFilterChange = (event) => setFilter(event.target.value)

  return (
    <div>
      <Header title="Phonebook"/>
      <div>
        filter shown with: <input value={filter} onChange={handleFilterChanger}/>
      </div>
      <Header title="add a new"/>
      <form onSubmit={addPerson}>
        <div>
          name: <input value={newName} onChange={handleNameChange}/>
        </div>
        <div>
          number: <input value={newNumber} onChange={handleNumberChange}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <Header title="Numbers"/>
      <div>
        {persons.map(person =>
          <Person key={person.name} personObject={person}/>
        )}
      </div>

    </div>
  )
}

export default App
