import { useState } from 'react'
import NewPersonForm from './components/NewPersonForm.js'

const Header = (props) => {
  const {title} = props;

  return <h2>{title}</h2>
}

const Person = (props) => {
  const {personObject} = props
  return <div>{personObject.name} {personObject.number}</div>
}

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '(123) 456-7890' }
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')




  const handleFilterChange = (event) => setFilter(event.target.value)

  const filteredPeople = persons.filter(person => person.name.toLowerCase().includes(filter))

  return (
    <div>
      <Header title="Phonebook"/>
      <div>
        filter shown with: <input value={filter} onChange={handleFilterChange}/>
      </div>
      <Header title="add a new"/>
      <NewPersonForm/>
      <Header title="Numbers"/>
      <div>
        {filteredPeople.map(person =>
          <Person key={person.name} personObject={person}/>
        )}
      </div>

    </div>
  )
}

export default App
