import { useEffect } from 'react'

const Notification = ({ message, showMessage, setShowMessage }) => {
  if (message === null) {
    return null
  }

  useEffect(() => {
    setTimeout(function() {
      setShowMessage(false)
    }, 3000)
  }, [])


  return (
    <div className='error'>
      {message}
    </div>
  )
}

export default Notification
